		public bool IsInteger(string 名字)
		{
			if (名字 == "")
			{
				return false;
			}
			int num = 0;
			while (true)
			{
				if (num < 名字.Length)
				{
					char c = 名字[num];
					if (!char.IsNumber(c))
					{
						break;
					}
					num++;
					continue;
				}
				return true;
			}
			return false;
		}

