		{
			int num = BitConverter.ToInt32(封包数据, 14);
			int num2 = BitConverter.ToInt32(封包数据, 22);
			switch (num)
			{
			case 800000047:
			{
				int num7 = 0;
				int num8 = 0;
				for (int k = 0; k < 96; k++)
				{
