					{
						num7 += BitConverter.ToInt32(装备栏包裹[k].物品数量, 0);
					}
					else if (BitConverter.ToInt32(装备栏包裹[k].物品ID, 0) == 1000000771 && BitConverter.ToInt32(装备栏包裹[k].物品数量, 0) >= 10 * num2)
					{
						num8 = 1;
