		public static string FilterSpecial(string str)
		{
			if (str.Length == 0)
			{
				return str;
			}
			str = str.Replace("'", string.Empty);
			str = str.Replace("<", string.Empty);
			str = str.Replace(">", string.Empty);
			str = str.Replace("%", string.Empty);
			str = str.Replace("'delete", string.Empty);
			str = str.Replace("''", string.Empty);
			str = str.Replace("\"\"", string.Empty);
			str = str.Replace(", ", string.Empty);
			str = str.Replace(".", string.Empty);
			str = str.Replace(">=", string.Empty);
			str = str.Replace("=<", string.Empty);
			str = str.Replace("-", string.Empty);
			str = str.Replace("_", string.Empty);
			str = str.Replace(";", string.Empty);
			str = str.Replace("||", string.Empty);
			str = str.Replace("[", string.Empty);
			str = str.Replace("]", string.Empty);
			str = str.Replace("&", string.Empty);
			str = str.Replace("#", string.Empty);
			str = str.Replace("/", string.Empty);
			str = str.Replace("-", string.Empty);
			str = str.Replace("|", string.Empty);
			str = str.Replace("?", string.Empty);
			str = str.Replace(">?", string.Empty);
			str = str.Replace("?<", string.Empty);
			str = str.Replace(" ", string.Empty);
			return str;
		}

