		private double GetDistance(Pointx p1, Pointx p2)
		{
			return Math.Sqrt(Math.Pow(Math.Max(p1.x, p2.x) - Math.Min(p1.x, p2.x), 2.0) + Math.Pow(Math.Max(p1.y, p2.y) - Math.Min(p1.y, p2.y), 2.0));
		}

