		public void 魔法使用(double mp)
		{
			try
			{
				switch (base.Player_Job)
				{
				case 5:
				{
					double num2 = base.医_太极心法 * 0.01;
					mp -= mp * num2;
					base.人物_MP -= (int)mp;
					break;
				}
				case 7:
					mp -= (double)(int)(mp * (base.琴师_高山流水 * 0.015));
					base.人物_MP -= (int)mp;
					break;
				case 13:
				{
					double num = base.神女太极心法 * 0.01;
					mp -= mp * num;
					base.人物_MP -= (int)mp;
					break;
				}
				default:
					base.人物_MP -= (int)mp;
					break;
				}
			}
			catch (Exception ex)
			{
				MainForm.WriteLine(1, "魔法使用" + base.人物全服ID + "|" + ToString() + " " + ex.Message);
			}
		}

