				{
					攻击力 = 0;
				}
				攻击确认时间 = 0;
				上次攻击人物ID = 人物ID;
				int 宠物攻击力 = -1;
				攻击类 攻击类2;
				if (World.TBL_KONGFU.TryGetValue(武功ID, out var value))
				{
					if (人物ID >= 10000)
					{
						if (value.FLD_TYPE == 4 || 触发流星漫天 || 触发杀星义气虎 || 触发杀星义气杀)
						{
							int num = value.FLD_攻击数量;
