						Buffer.BlockCopy(凝神珠包裹[i].物品全局ID, 0, array, 0, 8);
						Buffer.BlockCopy(BitConverter.GetBytes(1008000315), 0, array, 8, 4);
						Buffer.BlockCopy(BitConverter.GetBytes(1), 0, array, 12, 4);
