		public int 判断拳师连击(int 武功ID)
		{
			if (武功ID == 3000101 || 武功ID == 3000105)
			{
				if (拳师连击控制 == 0)
				{
					拳师连击控制 = 1;
					return 武功ID;
				}
				if (拳师连击控制 == 1)
				{
					拳师连击控制 = 2;
					return 武功ID + 1;
				}
				if (拳师连击控制 == 2)
				{
					拳师连击控制 = 3;
					return 武功ID + 2;
				}
				if (拳师连击控制 == 3)
				{
					拳师连击控制 = 4;
					return 武功ID + 3;
				}
				if (拳师连击控制 == 4)
				{
					拳师连击控制 = 5;
					return 3000109;
				}
				拳师连击控制 = 1;
				return 武功ID;
