					SaveGemData();
					SavePlayerData();
				}
				else
				{
					系统提示("必须达到" + World.转生需要等级 + "级和" + World.转生需要几转 + "转才能转生!", 10, "转生提示");
				}
			}
			catch (Exception ex)
